mapper.select:
   
		select  
    			t.id,
    			t.fromUserId,
    			t.toUserId,
    			t.content,
    			t.createTime,
    			t.state,
    			t.sendTime
		 from T_CHAT_MSG t
		
			 1=1 
    		
			 	 and  t.id   =  #{params.id}
    		
    		
			 	 and  t.fromUserId   =  #{params.fromUserId}
    		
    		
			 	 and  t.toUserId   =  #{params.toUserId}
    		
    		
			 	 and  t.content   =  #{params.content}
    		
    		
			 	 and  t.createTime   =  #{params.createTime}
    		
    		
			 	 and  t.state   =  #{params.state}
    		
    		
			 	 and  t.sendTime   =  #{params.sendTime}

mapper.update:
update T_CHAT_MSG
id=#{id}, fromUserId=#{fromUserId}, toUserId=#{toUserId}, content=#{content}, createTime=#{createTime}, state=#{state}, sendTime=#{sendTime},
where chatMsgid=#{chatMsgid}
