mapper.select:
   
		select  
    			t.videoid,
    			t.videoName,
    			t.videoUrl,
    			t.userid,
    			t.createtime,
    			t.clickNum,
    			t.videoDuration,
    			t.videoRecommend
		 from T_VIDEO t
		
			 1=1 
    		
			 	 and  t.videoid   =  #{params.videoid}
    		
    		
			 	 and  t.videoName   =  #{params.videoName}
    		
    		
			 	 and  t.videoUrl   =  #{params.videoUrl}
    		
    		
			 	 and  t.userid   =  #{params.userid}
    		
    		
			 	 and  t.createtime   =  #{params.createtime}
    		
    		
			 	 and  t.clickNum   =  #{params.clickNum}
    		
    		
			 	 and  t.videoDuration   =  #{params.videoDuration}
    		
    		
			 	 and  t.videoRecommend   =  #{params.videoRecommend}

mapper.update:
update T_VIDEO
videoid=#{videoid}, videoName=#{videoName}, videoUrl=#{videoUrl}, userid=#{userid}, createtime=#{createtime}, clickNum=#{clickNum}, videoDuration=#{videoDuration}, videoRecommend=#{videoRecommend},
where videoid=#{videoid}
